package day5;

public class BreakStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//break statement use for if we want to break the condition then we will use this statement
		for( int i=1;i<=10;i++) 
		{
			if (i==5) 
			{
				break;
			}
			System.out.println(i);
		}
		
		
	}

}
